import Layout from '../components/Layout';

import Flowers from '../components/Flowers';

import Link from 'next/link'
import { useUser } from '../utils/auth/useUser'

const Index = (props) => {
    const { user, logout } = useUser()

    if (!user) {
        return (
            <Layout>
                <div className="centerLayout">
                    <p>
                        You are not signed in. <Link href={'/auth'}><a>Sign in</a></Link>
                    </p>
                    <h1>Flowers A-Z</h1>
                    <Flowers data={props.data} />
                </div>
            </Layout>
        )
    }
    return (
        <Layout>
            <div className="centerLayout">
                <h1>Flowers A-Z</h1>
                <div>
                    <p>Signed in as {user.email}.</p><p className="signOut" onClick={() => logout()}>Log out</p>

                </div>
                <Flowers data={props.data} />
            </div>
            <style jsx>{`
                .signOut{
                    display: inline;
                }
            `}</style>
        </Layout>
    )
}

Index.getInitialProps = async function () {
    const res = await fetch('https://flowers-mock-data.firebaseio.com/flowers.json');
    const data = await res.json();

    return {
        data
    };
}


export default Index
