import Fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout';

/* const Flower = (props) => (
    <Layout>
        <div>
            <h1>{props.searchTerm ? props.searchTerm : null}</h1>
        </div>
    </Layout>
);

Flower.getInitialProps = async function () {
    const res = await fetch('https://flowers-mock-data.firebaseio.com/flowers.json');
    const data = await res.json();
    const searchTerm = window.location.search;
    return {
        data, searchTerm
    };
}

export default Flower; */

class Flower extends React.Component {
    state = {
        searchTerm: "",
        data: [],
    }

    async componentDidMount() {
        const res = await fetch('https://flowers-mock-data.firebaseio.com/flowers.json');
        const data = await res.json();
        this.setState({
            searchTerm: decodeURIComponent(window.location.search.slice(8)),
            data: data,
        });
    }

    render() {
        return (
            <Layout>
                <div className="centerLayout">

                    {
                        this.state.data.filter(flower => flower.common_name == this.state.searchTerm).map(flower => (
                            <>
                                <img src={flower.cover_image} alt="" />
                                <h1>{flower.common_name}</h1>
                                <p>Latin name: {flower.latin_name}</p>
                                <p>{flower.notes}</p>
                                <p>Blooming season: {flower.blooming_season}</p>
                                <h3>Soil:</h3>
                                {flower.soil.map(item => (
                                    <>
                                        <p>{item}</p>
                                    </>
                                ))}
                            </>
                        ))
                    }
                </div>
            </Layout>
        );
    }
}

export default Flower;
