import Comment from './Comment';

class Comments extends React.Component {
    state = {
        comments: [],
        loaded: false,
        index: null,
    }

    async componentDidMount() {
        const res = await fetch('https://flowers-mock-data.firebaseio.com/comments.json');
        const data = await res.json();
        this.setState({
            comments: Object.entries(data),
            loaded: true,
            index: this.props.index,
        });
    }

    render() {
        return (
            <div>
                <h2>Comments</h2>
                <form id="commentForm" action="">
                    <textarea name="" id="" cols="30" rows="2"></textarea>
                    <input type="submit" value="Comment" />
                </form>
                {this.state.loaded ? this.state.comments.map(user => (
                    user[1][this.state.index] ? (
                        Object.entries(user[1][this.state.index]).map((myComment, index) => (
                            myComment[1].comment ?
                                <div key={user[0] + index}>
                                    <Comment user={user[0]} comment={myComment[1].comment} />
                                </div>
                                : null
                        ))
                    ) : null
                )) : null}
                <style jsx>{`
                #commentForm{
                    display: flex;
                    width: 100%;
                }
                textarea {
                    resize: none;
                }
                `}</style>
            </div>
        );
    }
}

export default Comments;
