class Share extends React.Component {
    state = {
        show: false,
        link: ""
    }

    componentDidMount() {
        this.setState({
            link: window.location.href
        });
    }

    toggleShow = () => {
        if (this.state.show == false) {
            this.setState({
                show: true
            })
        } else {
            this.setState({
                show: false
            })
        }

    }

    render() {
        return (
            <div>
                <a onClick={this.toggleShow}>Share this page</a>
                {this.state.show ? <p>{this.state.link}</p> : null}

                <style jsx>{`
                /* .showButton{
                    background-color: darkseagreen;
                    color: white;
                    border: 2px solid;
                    border-radius: 32px;
                    padding: 1em;
                    cursor: pointer;
                } */


                `}</style>
            </div>
        );
    }
}

export default Share;
