import Comment from './Comment';

class Comments extends React.Component {
    /*     constructor() {
            super();
            this.post = this.post.bind(this);
        } */

    state = {
        comments: [],
        loaded: false,
        index: null,
        userComment: "",
    }

    async componentDidMount() {
        this.fetchComments();
        this.setState({
            index: this.props.index,
        });
    }

    //Fetches comments, both on page load and when a comment is posted from client
    fetchComments = async () => {
        const res = await fetch('https://flowers-mock-data.firebaseio.com/comments.json');
        const data = await res.json();
        this.setState({
            comments: Object.entries(data),
            loaded: true,
        });
    }

    handleChange = (e) => {
        this.setState({
            userComment: e.target.value
        })
    }

    post = async (message) => {

        if (message !== "") {
            await fetch(`https://flowers-mock-data.firebaseio.com/comments/MarcelR1998/${this.state.index}.json`, {
                method: 'post',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ comment: message })
            })
                .then(res => {
                    console.log(res)
                    this.fetchComments();
                })
                .catch(err => console.error(err))
        }
    }

    render() {
        return (
            <div>
                <h2>Comments</h2>
                <div id="commentForm">
                    <textarea id="" cols="30" rows="2" value={this.state.userComment} onChange={this.handleChange} />
                    <button onClick={() => this.post(this.state.userComment)}>Comment</button>
                </div>
                {this.state.loaded ? this.state.comments.map(user => (
                    user[1][this.state.index] ? (
                        Object.entries(user[1][this.state.index]).map((myComment, index) => (
                            myComment[1].comment ?
                                <div key={user[0] + index}>
                                    <Comment user={user[0]} comment={myComment[1].comment} />
                                </div>
                                : null
                        ))
                    ) : null
                )) : null}
                <style jsx>{`
                #commentForm{
                    display: flex;
                    width: 100%;
                }
                textarea {
                    resize: none;
                    width: 100%;
                }
                `}</style>
            </div>
        );
    }
}

export default Comments;
