import Comment from './Comment';
import { useEffect, useState } from 'react';
import { useUser } from '../utils/auth/useUser'

const Comments = (props) => {
    const [comments, setComments] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [index, setIndex] = useState(null);
    const [userComment, setUserComment] = useState("");
    const { user, logout } = useUser()
    console.log(user);

    useEffect(() => {
        fetchComments();
        setIndex(props.index)
    }, []);

    //Fetches comments, both on page load and when a comment is posted from client
    const fetchComments = async () => {
        const res = await fetch('https://flowers-mock-data.firebaseio.com/comments.json');
        const data = await res.json();
        setComments(Object.entries(data));
        setLoaded(true);
    }

    const handleChange = (e) => {
        setUserComment(e.target.value);
    }

    const post = async (message) => {
        if (message !== "") {
            await fetch(`https://flowers-mock-data.firebaseio.com/comments/MarcelR1998/${index}.json`, {
                method: 'post',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ comment: message })
            })
                .then(res => {
                    console.log(res)
                    fetchComments();
                    setUserComment("");
                })
                .catch(err => console.error(err))
        }
    }


    return (
        <div>
            <h2>Comments</h2>
            {user ? <div id="commentForm">
                <textarea id="" cols="30" rows="2" value={userComment} onChange={handleChange} />
                <button onClick={() => post(userComment)}>Comment</button>
            </div> : <div id="commentForm">
                    <textarea id="" cols="30" rows="2" value="Sign in in order to comment." />
                    <button disabled>Comment</button>
                </div>
            }

            {loaded ? comments.map(user => (
                user[1][index] ? (
                    Object.entries(user[1][index]).map((myComment, index) => (
                        myComment[1].comment ?
                            <div key={user[0] + index}>
                                <Comment user={user[0]} comment={myComment[1].comment} />
                            </div>
                            : null
                    ))
                ) : null
            )) : null}
            <style jsx>{`
                #commentForm{
                    display: flex;
                    width: 100%;
                }
                textarea {
                    resize: none;
                    width: 100%;
                }
                `}</style>
        </div>
    );

}

export default Comments;
