class Flowers extends React.Component {
    state = {
        search: "",
    }

    onchange = e => {
        this.setState({ search: e.target.value });
    }

    renderItem = flower => {
        const { search } = this.state;

        if (search !== "" && flower.common_name.toLowerCase().indexOf(search.toLowerCase()) === -1) {
            return null;
        }
        return <li key={flower.common_name}>
            {flower.cover_image ? <img src={flower.cover_image} alt="" /> : null}
            <div className="cardContent">
                <a href={`http://localhost:3000/flower?flower=${flower.common_name}`}>{flower.common_name}</a>
                <p>{flower.latin_name}</p>
                <p>{flower.sun ? "Planteras i solen" : "Planteras i skugga"}</p>
            </div>

            <style jsx>{`
                li {
                    list-style: none;
                    background-color: #fbfbfb;
                    /* box-shadow: 0px 7px 10px -5px rgba(0,0,0,0.33); */
                    display: flex;
                    padding: 1em;
                    margin-bottom: 1em;
                }

                .cardContent{
                    display: inline;
                    height: 128px;
                    margin: 0;
                }
                img {
                    height: 128px;
                    padding-right: 1em;
                }
                `}</style>
        </li>
    }

    render() {
        return (
            <div>
                <input type="text" placeholder="Search flower..." id="search" value={this.search} onChange={this.onchange} ></input>
                <ul className="list-group">
                    {this.props.data.map(flower => (
                        this.renderItem(flower)
                    ))}
                </ul>
                <br />

                <style jsx>{`
                ul {
                    padding-inline-start: 0;
                }
                
                li {
                    list-style: none;
                    background-color: #fbfbfb;
                    /* box-shadow: 0px 7px 10px -5px rgba(0,0,0,0.33); */
                    display: flex;
                    padding: 1em;
                }

                .cardContent{
                    display: inline;
                    height: 128px;
                    margin: 0;
                }
                img {
                    height: 128px;
                    padding-right: 1em;
                }
                `}</style>
            </div>
        );
    }
}

export default Flowers;
